INSERT INTO `article` VALUES ('6b8a5d717958', '关键词抽取', '关键词抽取', '# 关键词抽取\r\n\r\n```java\r\npackage cn.hutaotao.article.utils.article;\r\n\r\n/**\r\n * Created by ht on 2018/4/23.\r\n *\r\n * @author ht\r\n */\r\n\r\nimport cn.hutaotao.article.exception.MyException;\r\nimport lombok.extern.log4j.Log4j;\r\nimport org.apache.lucene.analysis.TokenStream;\r\nimport org.apache.lucene.analysis.tokenattributes.CharTermAttribute;\r\nimport org.wltea.analyzer.lucene.IKAnalyzer;\r\n\r\nimport java.io.IOException;\r\nimport java.io.StringReader;\r\nimport java.util.*;\r\nimport java.util.Map.Entry;\r\n\r\n/**\r\n * 获取文章关键字\r\n *\r\n * @author anwj\r\n */\r\n@Log4j\r\npublic class WordUtil {\r\n\r\n    /**\r\n     * 测试文章\r\n     */\r\n    static String keyWord = \"\";\r\n    /**\r\n     * 获取关键字个数\r\n     */\r\n    private final static Integer NUM = 10;\r\n    /**\r\n     * 截取关键字在几个单词以上的数量\r\n     */\r\n    private final static Integer QUANTITY = 1;\r\n\r\n    /**\r\n     * 传入String类型的文章，智能提取单词放入list中\r\n     *\r\n     * @param article\r\n     * @param a\r\n     * @return\r\n     * @throws IOException\r\n     */\r\n    private static List<String> extract(String article, Integer a) throws IOException {\r\n        List<String> list = new ArrayList<>();         //定义一个list来接收将要截取出来单词\r\n        IKAnalyzer analyzer = new IKAnalyzer();             //初始化IKAnalyzer\r\n        analyzer.setUseSmart(true);                         //将IKAnalyzer设置成智能截取\r\n        TokenStream tokenStream =                            //调用tokenStream方法(读取文章的字符流)\r\n                analyzer.tokenStream(\"\", new StringReader(article));\r\n        while (tokenStream.incrementToken()) {              //循环获得截取出来的单词\r\n            CharTermAttribute charTermAttribute =           //转换为char类型\r\n                    tokenStream.getAttribute(CharTermAttribute.class);\r\n            String keWord = charTermAttribute.toString();    //转换为String类型\r\n            if (keWord.length() > a) {                     //判断截取关键字在几个单词以上的数量(默认为2个单词以上)\r\n                list.add(keWord);                           //将最终获得的单词放入list集合中\r\n            }\r\n        }\r\n        return list;\r\n    }\r\n\r\n    /**\r\n     * 将list中的集合转换成Map中的key，value为数量默认为1\r\n     *\r\n     * @param list\r\n     * @return\r\n     */\r\n    private static Map<String, Integer> list2Map(List<String> list) {\r\n        Map<String, Integer> map = new HashMap<String, Integer>();\r\n        for (String key : list) {                      //循环获得的List集合\r\n            if (list.contains(key)) {              //判断这个集合中是否存在该字符串\r\n                map.put(key, map.get(key) == null ? 1 : map.get(key) + 1);\r\n            }                                       //将集中获得的字符串放在map的key键上\r\n        }                                          //并计算其value是否有值，如有则+1操作\r\n        return map;\r\n    }\r\n\r\n    /**\r\n     * 提取关键字方法\r\n     *\r\n     * @param article\r\n     * @param a\r\n     * @param n\r\n     * @return\r\n     * @throws IOException\r\n     */\r\n    public static String[] getKeyWords(String article, Integer a, Integer n) {\r\n\r\n        List<String> keyWordsList = null;            //调用提取单词方法\r\n        try {\r\n            keyWordsList = extract(article, a);\r\n        } catch (IOException e) {\r\n            log.error(\"提取关键字方法失败\");\r\n            e.printStackTrace();\r\n            throw new MyException(e);\r\n        }\r\n        Map<String, Integer> map = list2Map(keyWordsList);      //list转map并计次数\r\n        //使用Collections的比较方法进行对map中value的排序\r\n        ArrayList<Entry<String, Integer>> list = new ArrayList<>(map.entrySet());\r\n        Collections.sort(list, new Comparator<Map.Entry<String, Integer>>() {\r\n            public int compare(Map.Entry<String, Integer> o1, Map.Entry<String, Integer> o2) {\r\n                return (o2.getValue() - o1.getValue());\r\n            }\r\n        });\r\n        if (list.size() < n) n = list.size();                    //排序后的长度，以免获得到null的字符\r\n        String[] keyWords = new String[n];                    //设置将要输出的关键字数组空间\r\n        for (int i = 0; i < list.size(); i++) {                  //循环排序后的数组\r\n            if (i < n) {                                       //判断个数\r\n                keyWords[i] = list.get(i).getKey();           //设置关键字进入数组\r\n            }\r\n        }\r\n        return keyWords;\r\n    }\r\n\r\n    /**\r\n     * @param article\r\n     * @return\r\n     * @throws IOException\r\n     */\r\n    public static List<String> getKeyWords(String article) {\r\n        return Arrays.asList(getKeyWords(article, QUANTITY, NUM));\r\n    }\r\n\r\n    public static void main(String[] args) {\r\n\r\n        List keywords = getKeyWords(keyWord);\r\n        for (int i = 0; i < keywords.size(); i++) {\r\n            System.out.println(keywords.get(i));\r\n        }\r\n    }\r\n}\r\n\r\n```  \r\n', null, '/img/article_img_34.jpeg', '1524475028544', '1524594627960', '0', '1', '49', '0', '1', '1', '5f01e7e8b32a4c96b7e128cf04e86353', '4fb3b51da8b240a68734b7bcfb7351d1', '4482', '0');
INSERT INTO `article` VALUES ('6b8a84812755', '测试删除分类', '', 'echarts2的官方API里是带有字符云的,但到了echarts3就被从官网上移除了,想要使用的话可以从github上下载:\r\n\r\n下载地址:https://github.com/ecomfe/echarts-wordcloud\r\n\r\n使用方法:\r\n\r\n1.依次引入echarts.min.js和echarts-wordcloud.min.js(文件位置:下载的压缩包下dist/echarts-wordcloud.min.js);\r\n<script src=\"echarts.min.js\"></script>\r\n<script src=\"echarts-wordcloud.min.js\"></script>\r\n2.自己翻译的简易说明(供参考)\r\n\r\n width---字符云画布宽度\r\n\r\n height---字符云画布宽度\r\n\r\nmaskImage---剪影图像，白色区域将被排除在绘图文本之外,形状选项将继续应用为云的形状。', null, '', '1524585492600', '1524588405919', '0', '1', '9', '0', '0', '0', '346ded5ab8d2440c90f01143d099e8fc', '02a8e2aeab3c41c08e6b0a7acc337100', '418', '0');
