INSERT INTO `article` VALUES ('6b8a5d717958', '', '', '# \r\n\r\n```java\r\npackage cn.hutaotao.article.utils.article;\r\n\r\n/**\r\n * Created by ht on 2018/4/23.\r\n *\r\n * @author ht\r\n */\r\n\r\nimport cn.hutaotao.article.exception.MyException;\r\nimport lombok.extern.log4j.Log4j;\r\nimport org.apache.lucene.analysis.TokenStream;\r\nimport org.apache.lucene.analysis.tokenattributes.CharTermAttribute;\r\nimport org.wltea.analyzer.lucene.IKAnalyzer;\r\n\r\nimport java.io.IOException;\r\nimport java.io.StringReader;\r\nimport java.util.*;\r\nimport java.util.Map.Entry;\r\n\r\n/**\r\n * \r\n *\r\n * @author anwj\r\n */\r\n@Log4j\r\npublic class WordUtil {\r\n\r\n    /**\r\n     * \r\n     */\r\n    static String keyWord = \"\";\r\n    /**\r\n     * \r\n     */\r\n    private final static Integer NUM = 10;\r\n    /**\r\n     * \r\n     */\r\n    private final static Integer QUANTITY = 1;\r\n\r\n    /**\r\n     * Stringlist\r\n     *\r\n     * @param article\r\n     * @param a\r\n     * @return\r\n     * @throws IOException\r\n     */\r\n    private static List<String> extract(String article, Integer a) throws IOException {\r\n        List<String> list = new ArrayList<>();         //list\r\n        IKAnalyzer analyzer = new IKAnalyzer();             //IKAnalyzer\r\n        analyzer.setUseSmart(true);                         //IKAnalyzer\r\n        TokenStream tokenStream =                            //tokenStream()\r\n                analyzer.tokenStream(\"\", new StringReader(article));\r\n        while (tokenStream.incrementToken()) {              //\r\n            CharTermAttribute charTermAttribute =           //char\r\n                    tokenStream.getAttribute(CharTermAttribute.class);\r\n            String keWord = charTermAttribute.toString();    //String\r\n            if (keWord.length() > a) {                     //(2)\r\n                list.add(keWord);                           //list\r\n            }\r\n        }\r\n        return list;\r\n    }\r\n\r\n    /**\r\n     * listMapkeyvalue1\r\n     *\r\n     * @param list\r\n     * @return\r\n     */\r\n    private static Map<String, Integer> list2Map(List<String> list) {\r\n        Map<String, Integer> map = new HashMap<String, Integer>();\r\n        for (String key : list) {                      //List\r\n            if (list.contains(key)) {              //\r\n                map.put(key, map.get(key) == null ? 1 : map.get(key) + 1);\r\n            }                                       //mapkey\r\n        }                                          //value+1\r\n        return map;\r\n    }\r\n\r\n    /**\r\n     * \r\n     *\r\n     * @param article\r\n     * @param a\r\n     * @param n\r\n     * @return\r\n     * @throws IOException\r\n     */\r\n    public static String[] getKeyWords(String article, Integer a, Integer n) {\r\n\r\n        List<String> keyWordsList = null;            //\r\n        try {\r\n            keyWordsList = extract(article, a);\r\n        } catch (IOException e) {\r\n            log.error(\"\");\r\n            e.printStackTrace();\r\n            throw new MyException(e);\r\n        }\r\n        Map<String, Integer> map = list2Map(keyWordsList);      //listmap\r\n        //Collectionsmapvalue\r\n        ArrayList<Entry<String, Integer>> list = new ArrayList<>(map.entrySet());\r\n        Collections.sort(list, new Comparator<Map.Entry<String, Integer>>() {\r\n            public int compare(Map.Entry<String, Integer> o1, Map.Entry<String, Integer> o2) {\r\n                return (o2.getValue() - o1.getValue());\r\n            }\r\n        });\r\n        if (list.size() < n) n = list.size();                    //null\r\n        String[] keyWords = new String[n];                    //\r\n        for (int i = 0; i < list.size(); i++) {                  //\r\n            if (i < n) {                                       //\r\n                keyWords[i] = list.get(i).getKey();           //\r\n            }\r\n        }\r\n        return keyWords;\r\n    }\r\n\r\n    /**\r\n     * @param article\r\n     * @return\r\n     * @throws IOException\r\n     */\r\n    public static List<String> getKeyWords(String article) {\r\n        return Arrays.asList(getKeyWords(article, QUANTITY, NUM));\r\n    }\r\n\r\n    public static void main(String[] args) {\r\n\r\n        List keywords = getKeyWords(keyWord);\r\n        for (int i = 0; i < keywords.size(); i++) {\r\n            System.out.println(keywords.get(i));\r\n        }\r\n    }\r\n}\r\n\r\n```  \r\n', null, '/img/article_img_34.jpeg', '1524475028544', '1524594627960', '0', '1', '49', '0', '1', '1', '5f01e7e8b32a4c96b7e128cf04e86353', '4fb3b51da8b240a68734b7bcfb7351d1', '4482', '0');
